scriptlet.script:
myCaseType = caseType;
myCaseSubject = caseSubject;
myToMatchSubject = toMatchSubject;

if(myCaseType == "Incident" && myCaseSubject == myToMatchSubject)
{
    scriptletResponse = "success"; 
}
else
{
    scriptletResponse = "failure";
}

var myStatus = caseStatus;
if(myStatus == "Open - Auto Hold" ||
   myStatus == "Open - Pending Internal Fix" ||
   myStatus == "Open - Pending Vendor Fix" ||
   myStatus == "Open - Pending WO" ||
   myStatus == "Open - Savvis Hold" ||
   myStatus == "Open - Customer Hold" ||
   myStatus == "Open - Fixed")
{
      scriptletResponse = "success";
}
else
{
      scriptletResponse = "failure";
}

myErrMsg = scriptletContext.getLocal("errorMsg"); 
if(myErrMsg == null || myErrMsg == "undefined" || myErrMsg == "")
{
    scriptletContext.put("journalEntry","'Web Service getTicketById failed'");
}
else
{
    myErrMsg = "'" + myErrMsg + "'";
    scriptletContext.put("journalEntry",myErrMsg);
}

myCaseCount = scriptletContext.getLocal("caseCount"); 
myCaseId = caseId;
myCases = scriptletContext.getLocal("cases");

if(myCaseCount == null || myCaseCount == "undefined" || myCaseCount == "")
{
    myCaseCount=0;
}

myCaseCount++;


if(myCases == null || myCases == "undefined")
{
    myCases="";
}
if(myCases !="")
{
    myCases = myCases + " ";
}
myCases = myCases + myCaseId;



scriptletContext.put("caseCount", myCaseCount);
scriptletContext.put("cases", myCases);
