scriptlet.script:
var MAX_LEN = 253;

myJournal = ReshapeJournal(journalEntry);
if(myJournal == null)
{
    scriptletResponse = "failure";
}
else
{
    scriptletContext.put("journalEntry", myJournal);
    scriptletResponse = "success";
}



/// Perform basic syntax check, to ensure can be used in the "insert" SQL statement
/// Also truncate the overlength journalEntry to fit the MAX_LEN in NetCool and Impact
function ReshapeJournal( journalEntry )
{
   // sanity check
   if ( typeof(journalEntry) == "undefined" || journalEntry== null || journalEntry== "undefined" )
   {
      return null;
   }

   // the final result will be stored here
   var myOutput = new String();
   // temp local variable
   var myGrowSpace = MAX_LEN - myOutput.length;
   // temp local variable for input
   var myJournal = new String(journalEntry);
   myJournal = myJournal.replace(/^\s*/, "");
   myJournal = myJournal.replace(/\s*$/, "");

   while(myJournal.length > 0 && myGrowSpace > 0)
   {
      var token;

      // first, try to match "'######'"
      if( token = myJournal.match(/^'.*?'/))
      {
         token = new String(token);
         myJournal = myJournal.replace(token, "");
         if(token.length - 2 > myGrowSpace)
         { // hit the limit here
            token = token.substring(0, myGrowSpace + 1);
            token += "'";
         }
         myOutput += token;
         myGrowSpace -= token.length - 2;
      }
      // second, try to match "+char(10)"
      else if (token = myJournal.match(/^\s*\+\s*char\s*\(\s*\d+\s*\)/))
      {
         // could never hit limit here
         myJournal = myJournal.replace(token, "");
         myOutput += token;
         myGrowSpace -= 1;
      }
      // third, try to match "+"
      else if (token = myJournal.match(/^\s*\+\s*/))
      {
         myJournal = myJournal.replace(token, "");
         myOutput += token;
      }
      else
      {
         // encounted something invalid
         return null;
      }

      // remove prefix and postfix white space for each loop
      myJournal = myJournal.replace(/^\s*/, "");
      myJournal = myJournal.replace(/\s*$/, "");

   } // end of while loop

   return myOutput;
}
